function Get_Max (A, B : Integer) return Integer is
begin
  if A > B then
    return A;
  else
    return B;
  end if;
end Get_Max; -- ฟังก์ชันหาค่าสูงสุด

procedure Swap (A, B : in out Integer) is
  Temp : Integer;
begin
  Temp := A;
  A := B;
  B := Temp;
end Swap; -- Procedure สลับค่า (ใช้ in out parameter)

// ตัวอย่างที่ 1: การใช้ If-Else
procedure If_Example is
  My_Integer : Integer := 10;
begin
  if My_Integer > 0 then
    Put_Line ("Positive");
  else
    Put_Line ("Non-positive");
  end if;
end If_Example;
// ตัวอย่างที่ 2: การใช้วงลูป For
procedure For_Loop_Example is
begin
  for I in 1 .. 3 loop
    Put_Line ("Loop: " & Integer'Image(I));
  end loop;
end For_Loop_Example;
// ตัวอย่างที่ 3: การใช้ Case Statement
procedure Case_Example is
  type Day_Type is (Monday, Tuesday, Wednesday, Thursday, Friday, Saturday, Sunday);
  Today : Day_Type := Monday;
begin
  case Today is
    when Monday .. Friday =>
      Put_Line ("It's a weekday.");
    when Saturday | Sunday =>
      Put_Line ("It's the weekend!");
  end case;
end Case_Example;
// ตัวอย่างที่ 4: การใช้งาน Record Types (Structs)
procedure Record_Example is
  type Person_Type is record
    Name : String(1 .. 20);
    Age  : Integer;
  end record;
  John : Person_Type;
begin
  John.Name := "John Doe            "; -- ต้องกำหนดความยาวให้ตรง
  John.Age := 30;
  Put_Line ("Name: " & John.Name & ", Age: " & Integer'Image(John.Age));
end Record_Example;
// ตัวอย่างที่ 5: การจัดการข้อผิดพลาด (Exception Handling)
procedure Exception_Example is
  Numerator   : Integer := 10;
  Denominator : Integer := 0;
  Result      : Integer;
begin
  Result := Numerator / Denominator; -- จะเกิด Constraint_Error
  Put_Line ("Result: " & Integer'Image(Result));
exception
  when Constraint_Error =>
    Put_Line ("Error: Division by zero or other constraint violation.");
  when others =>
    Put_Line ("An unexpected error occurred.");
end Exception_Example;
