examples (ตัวอย่าง):
// ฟังก์ชันบวกเลขสองจำนวน
int add(int a, int b) {
  return a + b;
}
// ฟังก์ชันแสดงข้อความ
void print_message(char *msg) {
  printf("%s\n", msg);
}
// การเรียกใช้ฟังก์ชัน
int sum = add(5, 3); // sum จะมีค่า 8
print_message("Hello, C!\n"); // แสดงผล 'Hello, C!\n'
// ฟังก์ชันคำนวณ factorial แบบ recursive
long long factorial(int n) {
  if (n == 0)
    return 1;
  else
    return n * factorial(n - 1);
}
// การเรียกใช้ factorial
long long fact_5 = factorial(5); // fact_5 จะมีค่า 120

// ตัวอย่างที่ 1: การใช้เงื่อนไข (if-else)
int x = 10;
if (x > 5) {
  printf("x is greater than 5\n");
} else {
  printf("x is not greater than 5\n");
}

// ตัวอย่างที่ 2: การใช้วงลูป (for loop)
for (int i = 0; i < 3; i++) {
  printf("Loop: %d\n", i);
}

// ตัวอย่างที่ 3: การใช้ Switch Statement
char grade = 'B';
switch (grade) {
  case 'A':
    printf("Excellent!\n");
    break;
  case 'B':
    printf("Very Good!\n");
    break;
  case 'C':
    printf("Good!\n");
    break;
  default:
    printf("Pass\n");
}

// ตัวอย่างที่ 4: การใช้วงลูป (while loop)
int count = 0;
while (count < 3) {
  printf("Count is: %d\n", count);
  count++;
}

// ตัวอย่างที่ 5: การใช้วงลูป (do-while loop)
int i = 0;
do {
  printf("Do-While Count: %d\n", i);
  i++;
} while (i < 3);

// ตัวอย่างที่ 6: การใช้งาน Structure (โครงสร้างข้อมูล)
struct Person {
  char name[50];
  int age;
};
struct Person p1; // ประกาศตัวแปร struct
strcpy(p1.name, "Alice"); // กำหนดค่าให้กับสมาชิก name
p1.age = 30; // กำหนดค่าให้กับสมาชิก age
printf("Person: %s, %d years old.\n", p1.name, p1.age);

// ตัวอย่างที่ 7: การใช้งาน Pointer
int num = 100;
int *ptr = &num; // ptr เก็บที่อยู่ของ num
printf("Value of num: %d\n", num); // 100
printf("Address of num: %p\n", &num); // ที่อยู่หน่วยความจำของ num
printf("Value using pointer: %d\n", *ptr); // 100 (เข้าถึงค่าผ่าน pointer)

// ตัวอย่างที่ 8: การจัดการไฟล์ (File I/O) - การเขียนไฟล์
#include <stdio.h>
int main() {
  FILE *fp;
  fp = fopen("example.txt", "w"); // เปิดไฟล์เพื่อเขียน
  if (fp == NULL) {
    printf("Error opening file!\n");
    return 1;
  }
  fprintf(fp, "Hello from C file!\n"); // เขียนข้อมูลลงไฟล์
  fclose(fp); // ปิดไฟล์
  printf("File 'example.txt' created.\n");
  return 0;
}

// ตัวอย่างที่ 9: การจัดการสตริง (String Manipulation)
#include <string.h>
int main() {
  char str1[20] = "Hello";
  char str2[20];
  strcpy(str2, str1); // คัดลอก str1 ไปยัง str2
  printf("Copied string: %s\n", str2); // แสดงผล 'Hello'
  int len = strlen(str1); // หาความยาวของ str1
  printf("Length of str1: %d\n", len); // แสดงผล 5
  return 0;
}
