// ฟังก์ชันบวกเลขสองจำนวน
int add(int a, int b) {
  return a + b;
}
// ฟังก์ชันแสดงข้อความ
void printMessage(String msg) {
  print(msg);
}
// การเรียกใช้ฟังก์ชัน
int sum = add(5, 5); // sum จะมีค่า 10
printMessage("Hello, Dart!"); // แสดงผล 'Hello, Dart!'
// ฟังก์ชันที่มี Named Parameters และ Optional Parameters
void displayUserInfo({String? name, int? age}) {
  if (name != null) print('Name: $name');
  if (age != null) print('Age: $age');
}
displayUserInfo(name: 'Alice', age: 30); // เรียกใช้ด้วย Named Arguments
displayUserInfo(name: 'Bob'); // เรียกใช้โดยไม่ส่ง age
// Asynchronous Programming (Async/Await)
// Dart จัดการ Asynchronous Programming ได้ดีด้วย Future และ async/await
Future<String> fetchData() async {
  print("Fetching data...");
  await Future.delayed(Duration(seconds: 1)); // จำลองการหน่วงเวลา 1 วินาที
  print("Data fetched!");
  return "Data fetched asynchronously";
}
