// ตัวอย่างที่ 1: การใช้เงื่อนไข (if-else)
int score = 70;
if (score >= 60) {
  print('Passed');
} else {
  print('Failed');
}
// ตัวอย่างที่ 2: การใช้วงลูป (for loop)
for (int i = 0; i < 3; i++) {
  print('Loop Dart: $i');
}
// ตัวอย่างที่ 3: การใช้ Switch Statement
String command = 'OPEN';
switch (command) {
  case 'OPEN':
    print('Opening file...');
    break;
  case 'SAVE':
    print('Saving file...');
    break;
  default:
    print('Unknown command.');
}
// ตัวอย่างที่ 4: การใช้งาน Class และ Object-Oriented Programming (OOP)
class Car {
  String brand;
  int year;

  Car(this.brand, this.year); // Constructor แบบย่อ

  void drive() {
    print('$brand from $year is driving.');
  }
}
// สร้าง Object จากคลาส Car
Car myCar = Car('Toyota', 2023);
myCar.drive(); // แสดงผล 'Toyota from 2023 is driving.'
// ตัวอย่างที่ 5: Null Safety
String? nullableName = 'John'; // ตัวแปรที่สามารถเป็น null ได้
print(nullableName?.toUpperCase()); // ใช้ ?. เพื่อเรียกเมธอดแบบ Null-safe
nullableName = null;
print(nullableName?.toUpperCase()); // จะแสดง null (ไม่เกิด error)

String nonNullableName = nullableName ?? 'Guest'; // ใช้ ?? เพื่อให้ค่าเริ่มต้นหากเป็น null
print(nonNullableName); // จะแสดง 'Guest'
// ตัวอย่างที่ 6: การจัดการข้อผิดพลาด (Exception Handling)
try {
  int result = 10 ~/ 0; // ~/ คือ integer division
  print(result);
} on IntegerDivisionByZeroException {
  print('Error: Cannot divide by zero!');
} catch (e) {
  print('An unexpected error occurred: $e');
} finally {
  print('Operation completed.');
}
