// Procedure สำหรับคำนวณแรงขับดัน
PROCEDURE COMPUTE_THRUST (THRUST_VECTOR OUT VECTOR (3) REAL);
BEGIN
  THRUST_VECTOR(1) = 100.0; -- กำหนดค่าแกน X ของแรงขับ
  THRUST_VECTOR(2) = 50.0;  -- กำหนดค่าแกน Y ของแรงขับ
  THRUST_VECTOR(3) = 0.0;   -- กำหนดค่าแกน Z ของแรงขับ
END;

// Function สำหรับคำนวณขนาดของ Vector
FUNCTION VECTOR_MAGNITUDE (V VECTOR (3) REAL) RETURNS REAL;
BEGIN
  RETURN SQRT(V(1)**2 + V(2)**2 + V(3)**2);
END;

