// ฟังก์ชันบวกเลขสองจำนวน
fun sum(a: Int, b: Int): Int {
  return a + b
}
// ฟังก์ชันทักทาย
fun greet(name: String) {
  println("Hello, $name!")
}
// การเรียกใช้ฟังก์ชัน
val total = sum(2, 3) // total จะมีค่า 5
greet("Developer") // แสดงผล 'Hello, Developer!'
// ฟังก์ชันที่มี Default Arguments และ Named Arguments
fun calculateVolume(length: Int, width: Int = 1, height: Int = 1): Int {
  return length * width * height
}
println(calculateVolume(10)) // 10
println(calculateVolume(10, height = 5)) // 50 (ใช้ Named Arguments)
// ฟังก์ชัน Extension (เพิ่มฟังก์ชันให้คลาสที่มีอยู่แล้ว)
fun String.addExclamation(): String {
  return this + "!"
}
val text = "Hello".addExclamation() // Hello!
// Higher-Order Function (ฟังก์ชันที่รับฟังก์ชันเป็น Argument)
fun operate(a: Int, b: Int, operation: (Int, Int) -> Int): Int {
  return operation(a, b)
}
val result = operate(5, 3) { x, y -> x * y } // Result: 15
