declaration (การประกาศ):
function [output1, output2] = func_name(input1, input2)
  % ... (เนื้อหาฟังก์ชัน)
end % การประกาศฟังก์ชัน (สามารถคืนค่าได้หลายค่า)

examples (ตัวอย่าง):
function y = my_sum(a, b) % ฟังก์ชันบวกเลขสองจำนวน
  y = a + b;
end
function print_message(msg) % ฟังก์ชันแสดงข้อความ
  disp(msg);
end
// การเรียกใช้ฟังก์ชัน
result = my_sum(20, 30); % result จะมีค่า 50
print_message('Welcome to MATLAB!'); % แสดงผล 'Welcome to MATLAB!'
function [mean_val, std_dev] = calculate_stats(data) % ฟังก์ชันที่คืนค่าหลายค่า
  mean_val = mean(data);
  std_dev = std(data);
end
scores = [85 90 78 92 88];
[avg_score, std_score] = calculate_stats(scores); % รับค่าคืนหลายค่า
disp(['Average Score: ', num2str(avg_score)]);
disp(['Standard Deviation: ', num2str(std_score)]);
function plot_data(x_data, y_data, plot_title) % ฟังก์ชันสำหรับการสร้างกราฟ
  plot(x_data, y_data, '-o');
  title(plot_title);
  xlabel('X-axis');
  ylabel('Y-axis');
  grid on;
end
