examples (ตัวอย่าง):
// ฟังก์ชันทักทายรับพารามิเตอร์แรก
greet() {
    echo "Hello, $1!";
}
// ฟังก์ชันบวกเลขสองจำนวน
calculate_sum() {
    sum=$(($1 + $2));
    echo "Sum: $sum";
}
// ฟังก์ชันแสดงวันที่และเวลาปัจจุบัน
show_datetime() {
    date;
}

// ตัวอย่างที่ 1: การใช้เงื่อนไข (if-else)
if [ $age -ge 18 ]; then
    echo "Adult";
else
    echo "Minor";
fi
// ตัวอย่างที่ 2: การใช้วงลูป for
for file in *.txt; do
    echo "Processing $file";
done
// ตัวอย่างที่ 3: การรับอินพุตจากผู้ใช้
read -p "Enter your name: " user_name;
echo "Welcome, $user_name!";
// ตัวอย่างที่ 4: การทำงานกับไฟล์ (ตรวจสอบและสร้างไดเรกทอรี)
folder_name="my_scripts"
if [ ! -d "$folder_name" ]; then
    mkdir "$folder_name";
    echo "Directory '$folder_name' created.";
else
    echo "Directory '$folder_name' already exists.";
fi
