// Vertex Shader (คำนวณตำแหน่งของ Vertex)
#version 330 core
layout (location = 0) in vec3 aPos;
layout (location = 1) in vec3 aColor;

out vec3 vertexColor;

uniform mat4 model;
uniform mat4 view;
uniform mat4 projection;

void main()
{
    gl_Position = projection * view * model * vec4(aPos, 1.0);
    vertexColor = aColor;
}

// Fragment Shader (คำนวณสีของ Pixel)
#version 330 core
in vec3 vertexColor;
out vec4 FragColor;

void main()
{
    FragColor = vec4(vertexColor, 1.0);
}

// ฟังก์ชันที่ผู้ใช้กำหนด (User-defined Function)
float calculate_distance(vec3 p1, vec3 p2)
{
    return distance(p1, p2); // ใช้ built-in function distance
}

vec3 apply_light(vec3 surface_color, vec3 light_direction, vec3 normal)
{
    float diff = max(dot(normal, light_direction), 0.0);
    return surface_color * diff;
}

// ตัวอย่างที่ 1: การใช้เงื่อนไข (if-else) ใน Fragment Shader
#version 330 core
in vec3 vertexColor;
out vec4 FragColor;
uniform float threshold;

void main()
{
    if (vertexColor.r > threshold) {
        FragColor = vec4(1.0, 0.0, 0.0, 1.0); // สีแดงถ้าค่า R เกิน threshold
    } else {
        FragColor = vec4(vertexColor, 1.0); // ใช้สีเดิม
    }
}

// ตัวอย่างที่ 2: การใช้วงลูป for ใน Fragment Shader (เช่น สำหรับ Lighting)
#version 330 core
uniform int numLights;
uniform vec3 lightPositions[4];
uniform vec3 objectColor;
out vec4 FragColor;

void main()
{
    vec3 final_color = objectColor;
    for (int i = 0; i < numLights; i++) {
        // จำลองการคำนวณแสงแบบง่ายๆ
        final_color += objectColor * 0.1; // เพิ่มแสงทีละนิด
    }
    FragColor = vec4(final_color, 1.0);
}

// ตัวอย่างที่ 3: การใช้ Swizzling และ Vector Operations
vec4 myVector = vec4(1.0, 2.0, 3.0, 4.0);
vec2 xy = myVector.xy; // xy = (1.0, 2.0)
vec3 rgb = myVector.rgb; // rgb = (1.0, 2.0, 3.0)
vec4 rearranged = myVector.yxwz; // rearranged = (2.0, 1.0, 4.0, 3.0)

vec3 A = vec3(1.0, 0.0, 0.0);
vec3 B = vec3(0.0, 1.0, 0.0);
float dot_product = dot(A, B); // dot_product = 0.0
vec3 cross_product = cross(A, B); // cross_product = (0.0, 0.0, 1.0)
