examples (ตัวอย่าง):
// ฟังก์ชันบวกเลขสองจำนวน
(define (add a b) (+ a b))
// ฟังก์ชันทักทาย
(define (greet name) (display "Hello ") (display name) (newline))
// การเรียกใช้ฟังก์ชัน
(define sum (add 5 5)) ; sum จะมีค่า 10
(greet "World") ; แสดงผล 'Hello World!'
// ฟังก์ชัน Recursive สำหรับ Factorial
(define (factorial n)
  (if (= n 0)
      1
      (* n (factorial (- n 1))))) ; ถ้า n เป็น 0 คืน 1 ไม่งั้น n * factorial(n-1)
; (factorial 5) ; 120
// Higher-Order Function (ฟังก์ชันที่รับฟังก์ชันเป็น Argument)
(define (apply-twice f x) (f (f x)))
; (apply-twice (lambda (y) (+ y 1)) 5) ; 7 (5 -> 6 -> 7)
// การทำงานกับ List โดยใช้ Recursion
(define (list-sum lst)
  (if (null? lst)
      0
      (+ (car lst) (list-sum (cdr lst))))) ; ถ้า List ว่าง คืน 0 ไม่งั้น Head + Sum ของ Tail
; (list-sum '(1 2 3 4)) ; 10
