// Entity และ Architecture: Half Adder (วงจรบวกครึ่งหนึ่ง)
entity half_adder is
  port (
    a, b    : in  std_logic;
    sum, carry : out std_logic
  );
end half_adder;

architecture behavioral of half_adder is
begin
  sum <= a xor b;
  carry <= a and b;
end behavioral;
// Process (Always Block): D Flip-Flop
entity d_flip_flop is
  port (
    clk : in  std_logic;
    d   : in  std_logic;
    q   : out std_logic
  );
end d_flip_flop;

architecture rtl of d_flip_flop is
begin
  process (clk) -- เมื่อ clk เปลี่ยนแปลง
  begin
    if rising_edge(clk) then -- เมื่อ clock ขอบขาขึ้น
      q <= d; -- กำหนดค่า d ให้ q (Non-blocking assignment)
    end if;
  end process;
end rtl;
// Process: Counter 8-bit พร้อม Synchronous Reset
entity counter_8bit is
  port (
    clk   : in  std_logic;
    reset : in  std_logic;
    count : out std_logic_vector(7 downto 0)
  );
end counter_8bit;

architecture behavioral of counter_8bit is
  signal internal_count : std_logic_vector(7 downto 0) := (others => '0');
begin
  process (clk, reset)
  begin
    if rising_edge(reset) then -- ถ้า reset เป็น High (Synchronous Reset)
      internal_count <= (others => '0'); -- Reset counter เป็น 0
    elsif rising_edge(clk) then -- เมื่อ clock ขอบขาขึ้น
      internal_count <= internal_count + 1; -- นับเพิ่มทีละ 1
    end if;
  end process;
  count <= internal_count; -- กำหนดค่า Internal_count ให้ Output Port
end behavioral;
// Testbench (สำหรับจำลอง Simulation)
entity testbench is
end testbench;

architecture testbench_arch of testbench is
  signal clk, reset : std_logic := '0';
  signal counter_out : std_logic_vector(7 downto 0);
  constant CLK_PERIOD : time := 10 ns;
begin
  -- Instantiate DUT (Device Under Test)
  DUT : entity work.counter_8bit
    port map (
      clk   => clk,
      reset => reset,
      count => counter_out
    );

  -- Generate Clock
  clk_process : process
  begin
    loop
      clk <= '0';
      wait for CLK_PERIOD / 2;
      clk <= '1';
      wait for CLK_PERIOD / 2;
    end loop;
  end process clk_process;

  -- Generate Reset and Stimulus
  stimulus_process : process
  begin
    reset <= '1';
    wait for 20 ns; -- Reset active for 20 ns
    reset <= '0';
    wait for 100 ns; -- Run for 100 ns
    report "Simulation Finished"; -- แสดงข้อความใน Simulation log
    wait; -- หยุด Simulation
  end process stimulus_process;
end testbench_arch;

// ตัวอย่างที่ 1: การใช้ If-Else ใน Process (Combinational Logic)
architecture comb_logic of example is
  signal a, b, sel : std_logic;
  signal result : std_logic;
begin
  process (a, b, sel)
  begin
    if sel = '1' then
      result <= a;
    else
      result <= b;
    end if;
  end process;
end comb_logic;
// ตัวอย่างที่ 2: การใช้วงลูป For ใน Process (Simulation หรือ Synthesizable Logic บางกรณี)
architecture loop_example of example is
  signal data_in : std_logic_vector(3 downto 0);
  signal sum_bits : std_logic;
begin
  process (data_in)
    variable temp_sum : std_logic := '0';
  begin
    temp_sum := '0';
    for i in 0 to 3 loop
      temp_sum := temp_sum xor data_in(i);
    end loop;
    sum_bits <= temp_sum;
  end process;
end loop_example;
// ตัวอย่างที่ 3: การใช้ Case Statement ใน Process
architecture case_example of example is
  type state_type is (IDLE, READ, WRITE);
  signal current_state : state_type := IDLE;
  signal opcode : std_logic_vector(1 downto 0);
  signal next_state : state_type;
begin
  process (current_state, opcode)
  begin
    case current_state is
      when IDLE =>
        if opcode = "01" then
          next_state <= READ;
        else
          next_state <= IDLE;
        end if;
      when READ =>
        next_state <= IDLE;
      when WRITE =>
        next_state <= IDLE;
      when others => -- ต้องมี when others สำหรับทุกกรณีที่เป็นไปได้ (Synthesizable)
        next_state <= IDLE;
    end case;
  end process;
end case_example;
