// ตัวอย่างที่ 5: การจัดการข้อผิดพลาด (Assertion) ใน Simulation
architecture assertion_example of example is
  signal data_valid : boolean := false;
  signal count_val : integer := 0;
begin
  process (clk) -- สมมติมี clk
  begin
    if rising_edge(clk) then
      -- Simulate some logic
      if count_val = 5 then
        data_valid <= true;
      else
        count_val <= count_val + 1;
      end if;
    end if;
  end process;

  -- Assertion: data_valid should be true after count_val reaches 5
  -- assert data_valid report "Data did not become valid after counter reached 5" severity ERROR;
  -- (Assertion สามารถวางใน process หรือเป็น concurrent statement ก็ได้)
end assertion_example;
