declaration (การประกาศ):
// Class member function (ฟังก์ชันสมาชิกคลาส)
class MyClass { 
public: 
  void myMethod(); 
};

examples (ตัวอย่าง):
// ฟังก์ชันคูณเลขสองจำนวน
int multiply(int a, int b) {
  return a * b;
}
// ฟังก์ชันแสดงผล Vector (ใช้ STL)
#include <vector>
#include <iostream>
void print_vector(const std::vector<int>& vec) {
  for (int x : vec) { 
    std::cout << x << " "; 
  }
  std::cout << std::endl;
}
// ฟังก์ชัน Overloading (ชื่อฟังก์ชันเดียวกันแต่พารามิเตอร์ต่างกัน)
int add(int a, int b) { return a + b; }
double add(double a, double b) { return a + b; }
// การเรียกใช้ฟังก์ชัน
int product = multiply(4, 2); // product จะมีค่า 8
std::vector<int> numbers = {1, 2, 3};
print_vector(numbers); // แสดงผล '1 2 3'
// การเรียกใช้ฟังก์ชัน Overloading
int sum_int = add(10, 20); // เรียก add(int, int)
double sum_double = add(10.5, 20.5); // เรียก add(double, double)

// ตัวอย่างที่ 1: การใช้เงื่อนไข (if-else)
#include <iostream>
int val = 20;
if (val > 15) {
  std::cout << "Val is greater than 15" << std::endl;
} else {
  std::cout << "Val is not greater than 15" << std::endl;
}
// ตัวอย่างที่ 2: การใช้วงลูป (for loop)
#include <iostream>
for (int i = 0; i < 3; i++) {
  std::cout << "Loop C++: " << i << std::endl;
}
// ตัวอย่างที่ 3: การใช้ Switch Statement
#include <iostream>
char grade = 'B';
switch (grade) {
  case 'A':
    std::cout << "Excellent!" << std::endl;
    break;
  case 'B':
    std::cout << "Very Good!" << std::endl;
    break;
  default:
    std::cout << "Pass" << std::endl;
}
// ตัวอย่างที่ 4: การใช้งานคลาสและ Object-Oriented Programming (OOP)
#include <iostream>
#include <string>
class Dog {
public:
  std::string name;
  int age;
  void bark() {
    std::cout << name << " says Woof!" << std::endl;
  }
};
Dog myDog; // สร้าง Object จากคลาส Dog
myDog.name = "Buddy";
myDog.age = 3;
myDog.bark(); // เรียกใช้เมธอดของ Object
// ตัวอย่างที่ 5: การจัดการหน่วยความจำด้วย Pointer (Memory Management with Pointers)
#include <iostream>
int* dynamic_int = new int; // จัดสรรหน่วยความจำสำหรับ int
*dynamic_int = 100; // กำหนดค่า
std::cout << "Dynamic int value: " << *dynamic_int << std::endl;
delete dynamic_int; // คืนหน่วยความจำ
dynamic_int = nullptr; // ตั้งค่าเป็น nullptr เพื่อป้องกัน Dangling Pointer
