examples (ตัวอย่าง):
def calculate_area(width, height): // ฟังก์ชันคำนวณพื้นที่
  return width * height
add = lambda x, y: x + y // ฟังก์ชัน add แบบย่อ (Lambda Function) เพื่อบวกเลข
def greet(name='Guest'): // ฟังก์ชันพร้อมค่าเริ่มต้นสำหรับพารามิเตอร์
  return f'Hello, {name}!'
print(greet()) // Hello, Guest!
print(greet('Alice')) // Hello, Alice!
def sum_all(*args): // ฟังก์ชันที่รับจำนวนอาร์กิวเมนต์แบบไม่จำกัด
  return sum(args)
print(sum_all(1, 2, 3)) // 6
def build_profile(first, last, **kwargs): // ฟังก์ชันที่รับ keyword arguments แบบไม่จำกัด
  profile = {'first_name': first, 'last_name': last}
  profile.update(kwargs)
  return profile
print(build_profile('John', 'Doe', age=30, city='New York'))
def create_adder(x): // ฟังก์ชันที่คืนค่าฟังก์ชัน (Closure)
  def adder(y):
    return x + y
  return adder
add_5 = create_adder(5)
print(add_5(10)) // 15
def count_up_to(max): // ฟังก์ชัน Generator
  i = 1
  while i <= max:
    yield i
    i += 1

counter = count_up_to(3)
print(next(counter)) // 1
print(next(counter)) // 2
