// ตัวอย่างที่ 4: การใช้งาน Class และ Inheritance (OOP)
class Vehicle {
  var brand: String
  init(brand: String) { self.brand = brand }
  func drive() { print("\(brand) is driving.") }
}
class Car: Vehicle {
  var model: String
  init(brand: String, model: String) {
    self.model = model
    super.init(brand: brand)
  }
  override func drive() { // Override เมธอดจาก Parent Class
    print("\(brand) \(model) is driving fast!")
  }
}
let myCar = Car(brand: "Honda", model: "Civic")
myCar.drive() // แสดงผล 'Honda Civic is driving fast!'
// ตัวอย่างที่ 5: การจัดการข้อผิดพลาด (Error Handling) ด้วย Do-Catch
enum NetworkError: Error {
  case badURL
  case serverError(code: Int)
}
func fetchData(from urlString: String) throws -> String {
  guard let url = URL(string: urlString) else { throw NetworkError.badURL }
  // จำลองการเรียก API
  if urlString.contains("error") { throw NetworkError.serverError(code: 500) }
  return "Data from \(urlString)"
}
do {
  let data = try fetchData(from: "https://api.example.com/data")
  print(data)
  let errorData = try fetchData(from: "https://api.example.com/error")
  print(errorData)
} catch NetworkError.badURL {
